import { useContext } from "react";
import CartContext from "../../store/cart-context";
import Modal from "../UI/Modal";
import classes from "./Cart.module.css";
import CartItem from './CartItem';
const Cart = (props) => {
  const cartCtx = useContext(CartContext);
  const totalAmount =  `${cartCtx.totalAmount}원` ;
  const hasItems =  cartCtx.items.length > 0 ;
  const cartItemRemove = () => {

  }
  const cartItems = (
    <ul className={classes['cart-items']}>
      {cartCtx.items.map((item) => (
        <CartItem key={item.id} name={item.name} price={item.price} amount={item.amount} >{item.name}</CartItem>
      ))}
    </ul>
  );
  return (
    <Modal onClick={props.onClose}>

      {cartItems}
      <div className={classes.total}>
        <span>총 주문금액</span>
        <span>{totalAmount}</span>
      </div>
      <div className={classes.actions}>
        <button className={classes['button--alt']} onClick={props.onClose}>닫기</button>
        {hasItems && <button className={classes.button}>주문하기</button> }

      </div>
    </Modal>
  );
};
export default Cart;
